package org.bedework.cache.core.beans;

import java.io.Serializable;

/**
 * A simple bean used as the key into the cache.  The cache key includes
 * information sufficient to uniquely identify a proxied+cached request.
 *
 * @author eric.wittmann@redhat.com
 */
public class CacheKeyBean implements Serializable {

    private static final long serialVersionUID = -6477765778885476432L;
    
    private String method;
    private String uri;

    /**
     * Constructor.
     */
    public CacheKeyBean() {
    }

    /**
     * Constructor.
     * @param method
     * @param uri
     */
    public CacheKeyBean(String method, String uri) {
        setMethod(method);
        setUri(uri);
    }

    /**
     * @return the method
     */
    public String getMethod() {
        return method;
    }

    /**
     * @param method the method to set
     */
    public void setMethod(String method) {
        this.method = method;
    }

    /**
     * @return the uri
     */
    public String getUri() {
        return uri;
    }

    /**
     * @param uri the uri to set
     */
    public void setUri(String uri) {
        this.uri = uri;
    }

    /**
     * @see java.lang.Object#hashCode()
     */
    @Override
    public int hashCode() {
        final int prime = 31;
        int result = 1;
        result = prime * result + ((method == null) ? 0 : method.hashCode());
        result = prime * result + ((uri == null) ? 0 : uri.hashCode());
        return result;
    }

    /**
     * @see java.lang.Object#equals(java.lang.Object)
     */
    @Override
    public boolean equals(Object obj) {
        if (this == obj)
            return true;
        if (obj == null)
            return false;
        if (getClass() != obj.getClass())
            return false;
        CacheKeyBean other = (CacheKeyBean) obj;
        if (method == null) {
            if (other.method != null)
                return false;
        } else if (!method.equals(other.method))
            return false;
        if (uri == null) {
            if (other.uri != null)
                return false;
        } else if (!uri.equals(other.uri))
            return false;
        return true;
    }
    
}
